import React, { FC, useState, useEffect } from 'react';
import Chessboard from 'chessboardjsx';
import { makeStyles } from '@material-ui/core/styles';
import { ChessInstance, Square, Chess } from 'chess.js'

const ChessReq:any = require('chess.js');

const useStyles = makeStyles({
  board: {
    marginTop: '20px',
    boxShadow: '5px 5px 5px grey',
  }
});

enum Turn {
  W = 'W',
  B = 'B',
};

type Move = {
  sourceSquare: Square,
  targetSquare: Square,
};

const Board: FC = () => {
  const classes = useStyles();

  const [game] = useState<ChessInstance>(new ChessReq());
  const [turn, setTurn] = useState<Turn>(Turn.W);
  const [fen, setFen] = useState("start");

  useEffect(() => {
    if (game.game_over()) return;

    if (turn === Turn.B) {
      setTimeout(() => {
        const newFen = randomMove(game)
        if (!newFen) return; // no more legal moves
        setTurn(Turn.W);
        setFen(newFen);
      }, 1000);
    }

  }, [turn]);

  const randomMove = (game: ChessInstance): string | null => {
    const moves = game.moves();
    const move = moves[Math.floor(Math.random() * moves.length)];
    if (game.move(move) !== null) {
      return game.fen();
    }
    return null;
  }

  const onDrop = ({ sourceSquare, targetSquare }: Move) => {
    let move = game.move({
      from: sourceSquare,
      to: targetSquare,
      promotion: 'q', // always promote to queen
    })

    if (move === null) return;

    setTurn(Turn.B);

    setFen(game.fen());
  };

  return (
    <div className={classes.board}>
      <Chessboard 
        position={fen}
        onDrop={onDrop}
        transitionDuration={500}
      />
    </div>
  );
};

export default Board;
